mapper.insert:
INSERT INTO POINT (
                    USER_SEQ,
                    RENTAL_SEQ,
                    PREVIOUS_POINT, 
                    POINT_STATUS, 
                    POINT_TRANSACTION, 
                    TOTAL_POINT
                    )
            VALUE (
                     #{userSeq}
                    ,#{rentalSeq}
                    ,#{previousPoint}
                    ,#{pointStatus}
                    ,#{pointTransaction}
                    ,#{previousPoint} + #{pointTransaction} 
                    )
