mapper.select:
SELECT R.RENTAL_SEQ, R.BOOK_SEQ, B.BOOK_TITLE AS BOOK_TITLE, R.USER_SEQ, R.RENTAL_DT, R.PREDICT_RETURN_DT, R.RETURN_DT, R.RENTAL_STATUS, R.RENTAL_REG_DT, R.RENTAL_UPDATE_DT FROM RENTAL R JOIN ( SELECT BOOK_SEQ, BOOK_TITLE FROM BOOK ) B ON R.BOOK_SEQ = B.BOOK_SEQ
WHERE R.USER_SEQ = #{userSeq}
WHERE R.RENTAL_STATUS = 0

mapper.insert:
INSERT INTO RENTAL (
                    BOOK_SEQ,
                    USER_SEQ,
                    PREDICT_RETURN_DT
                    )
            VALUES (
                    #{bookSeq},
                    #{userSeq},
                    #{predictReturnDt}
                   )
